import React, { useEffect, useState } from 'react';
import { getReports } from '../../api';
import { InspectionReport } from '../../types';
import './ViewReports.css'; // Create this CSS file

// Temporary mock auth hook for development
const useMockAuth = () => {
  return {
    user: {
      id: 'temp-user-id',
      email: 'temp@example.com',
      name: 'Temp User',
      role: 'owner'
    }
  };
};

const ViewReports: React.FC = () => {
    const [reports, setReports] = useState<InspectionReport[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const [expandedReport, setExpandedReport] = useState<string | null>(null);

    const { user } = useMockAuth();

    useEffect(() => {
        const fetchReports = async () => {
            if (!user?.id) {
                setError('User not authenticated');
                setLoading(false);
                return;
            }

            try {
                const fetchedReports = await getReports(user.id);
                setReports(fetchedReports);
            } catch (err) {
                setError('Failed to fetch reports. Please try again later.');
                console.error('Error fetching reports:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchReports();
    }, [user?.id]);

    const toggleReport = (reportId: string) => {
        setExpandedReport(expandedReport === reportId ? null : reportId);
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
                <p>Loading your inspection reports...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <div className="error-icon">!</div>
                <p>{error}</p>
                <button 
                    className="retry-button"
                    onClick={() => window.location.reload()}
                >
                    Try Again
                </button>
            </div>
        );
    }

    return (
        <div className="reports-view">
            <div className="reports-header">
                <h2>Your Inspection Reports</h2>
                <div className="reports-summary">
                    <span className="total-reports">
                        {reports.length} {reports.length === 1 ? 'Report' : 'Reports'}
                    </span>
                    <span className="filter-controls">
                        Filter: <select>
                            <option>All</option>
                            <option>Passed</option>
                            <option>Failed</option>
                            <option>Pending</option>
                        </select>
                    </span>
                </div>
            </div>

            {reports.length === 0 ? (
                <div className="empty-state">
                    <img src="/images/no-reports.svg" alt="No reports" />
                    <h3>No inspection reports found</h3>
                    <p>You don't have any inspection reports yet.</p>
                </div>
            ) : (
                <div className="reports-grid">
                    {reports.map(report => (
                        <div 
                            key={report.id} 
                            className={`report-card ${expandedReport === report.id ? 'expanded' : ''}`}
                            onClick={() => toggleReport(report.id)}
                        >
                            <div className="card-header">
                                <div className="report-id">
                                    <span className="report-icon">📋</span>
                                    <h3>IN-{report.id.slice(0, 8).toUpperCase()}</h3>
                                </div>
                                <span className={`status-badge ${report.status.toLowerCase()}`}>
                                    {report.status}
                                </span>
                            </div>

                            <div className="card-meta">
                                <div className="meta-item">
                                    <span className="meta-label">Vehicle:</span>
                                    <span className="meta-value">VH-{report.vehicleId.slice(0, 6)}</span>
                                </div>
                                <div className="meta-item">
                                    <span className="meta-label">Date:</span>
                                    <span className="meta-value">
                                        {new Date(report.date).toLocaleDateString('en-US', {
                                            year: 'numeric',
                                            month: 'short',
                                            day: 'numeric'
                                        })}
                                    </span>
                                </div>
                            </div>

                            {expandedReport === report.id && (
                                <div className="card-details">
                                    <div className="details-section">
                                        <h4>Inspection Details</h4>
                                        <p>{report.details}</p>
                                    </div>
                                    <div className="details-section">
                                        <h4>Inspector Notes</h4>
                                        <p>{report.notes || 'No additional notes provided'}</p>
                                    </div>
                                    <div className="action-buttons">
                                        <button className="download-button">
                                            Download PDF
                                        </button>
                                        <button className="share-button">
                                            Share Report
                                        </button>
                                    </div>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ViewReports;
