import React, { useEffect, useState } from 'react';
import { getReports } from '../../api';
import { InspectionReport } from '../../types';

// Temporary mock auth hook for development
const useMockAuth = () => {
  return {
    user: {
      id: 'temp-user-id', // Remove this in production
      email: 'temp@example.com',
      name: 'Temp User',
      role: 'owner'
    }
  };
};

const ViewReports: React.FC = () => {
    const [reports, setReports] = useState<InspectionReport[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    // Use mock auth for development - replace with real auth in production
    // const { user } = useAuth(); // Use this when AuthContext is set up
    const { user } = useMockAuth(); // Temporary for development

    useEffect(() => {
        const fetchReports = async () => {
            if (!user?.id) {
                setError('User not authenticated');
                setLoading(false);
                return;
            }

            try {
                const fetchedReports = await getReports(user.id);
                console.log('Fetched reports:', fetchedReports);
                setReports(fetchedReports);
            } catch (err) {
                setError('Failed to fetch reports. Please try again later.');
                console.error('Error fetching reports:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchReports();
    }, [user?.id]);

    if (loading) {
        return <div className="loading-spinner">Loading reports...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="reports-container">
            <h2>Your Inspection Reports</h2>
            {reports.length === 0 ? (
                <p className="no-reports">No inspection reports found.</p>
            ) : (
                <ul className="reports-list">
                    {reports.map(report => (
                        <li key={report.id} className="report-card">
                            <div className="report-header">
                                <h3>Inspection #{report.id.slice(0, 8)}</h3>
                                <span className={`status-badge ${report.status.toLowerCase()}`}>
                                    {report.status}
                                </span>
                            </div>
                            <p className="report-meta">
                                <strong>Date:</strong> {new Date(report.date).toLocaleDateString()}
                            </p>
                            <div className="report-details">
                                <p>{report.details}</p>
                            </div>
                            <div className="report-footer">
                                <span>Vehicle ID: {report.vehicleId}</span>
                                <span>Inspector ID: {report.inspectorId}</span>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ViewReports;
