import React, { useState } from 'react';
import VehicleDetails from '../components/Owner/VehicleDetails';
import BookAppointment from '../components/Owner/BookAppointment';
import ViewReports from '../components/Owner/ViewReports';
import './OwnerDashboard.css';

// Corrected user hook
const useUser = () => {
  const userData = localStorage.getItem('user');
  return userData ? JSON.parse(userData) : { name: 'Owner' };
};

const OwnerDashboard: React.FC = () => {
    const user = useUser();
    const [report, setReport] = useState({
        type: '',
        description: ''
    });
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleReportSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Report submitted:', report);
        setIsSubmitted(true);
        setTimeout(() => setIsSubmitted(false), 3000);
        setReport({ type: '', description: '' });
    };

    return (
        <div className="owner-dashboard">
            <header className="dashboard-header">
                <h1>Vehicle Owner Dashboard</h1>
                <div className="user-profile">
                    <span className="welcome-message">Welcome back, {user.name}</span>
                    <div className="avatar">
                        {user.name.charAt(0).toUpperCase()}
                    </div>
                </div>
            </header>

            {/* Rest of your component remains unchanged */}
            {/* ... */}
        </div>
    );
};

export default OwnerDashboard;
