import React, { useState } from 'react';

const UploadReport: React.FC = () => {
    const [file, setFile] = useState<File | null>(null);
    const [typedReport, setTypedReport] = useState<string>('');
    const [message, setMessage] = useState<string>('');

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0]) {
            setFile(event.target.files[0]);
        }
    };

    const handleTypedReportChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setTypedReport(event.target.value);
    };

    const handleUpload = async () => {
        const formData = new FormData();

        if (file) {
            formData.append('report', file);
        } else if (typedReport.trim()) {
            formData.append('reportText', typedReport);
        } else {
            setMessage('Please select a file or type a report.');
            return;
        }

        try {
            const response = await fetch('/api/upload-report', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                setMessage('Report uploaded successfully.');
                setFile(null);
                setTypedReport('');
            } else {
                setMessage('Failed to upload report.');
            }
        } catch (error) {
            setMessage('An error occurred while uploading the report.');
        }
    };

    return (
        <div>
            <h2>Upload or Type Inspection Report</h2>
            <input type="file" onChange={handleFileChange} />
            <p>OR</p>
            <textarea
                placeholder="Type your report here..."
                value={typedReport}
                onChange={handleTypedReportChange}
                rows={8}
                style={{ width: '100%' }}
            />
            <br />
            <button onClick={handleUpload}>Upload</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default UploadReport;
