import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState<'inspector' | 'owner'>('owner');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1500));

            // Create user object with role
            const user = {
                email,
                role,
                // Add other user data you might need
                username: email.split('@')[0] // Example: extract username from email
            };

            // Store user data in localStorage
            localStorage.setItem('currentUser', JSON.stringify(user));

            // Redirect based on role
            if (role === 'inspector') {
                navigate('/inspectordashboard', { replace: true });
            } else {
                navigate('/ownerdashboard', { replace: true });
            }

        } catch (err) {
            setError('Login failed. Please check your credentials.');
        } finally {
            setIsLoading(false);
        }
    };

    // ... rest of your component remains the same ...
};
