import React, { useState, useRef, useEffect } from 'react';
import './ScanPlate.css';

const ScanPlate: React.FC = () => {
    const [plateNumber, setPlateNumber] = useState('');
    const [verificationResult, setVerificationResult] = useState<string | null>(null);
    const [scanMethod, setScanMethod] = useState<'camera' | 'manual'>('manual'); // Default to manual
    const [cameraActive, setCameraActive] = useState(false);
    const videoRef = useRef<HTMLVideoElement>(null);
    const canvasRef = useRef<HTMLCanvasElement>(null);

    // Clean up camera on unmount or mode change
    useEffect(() => {
        return () => {
            if (videoRef.current?.srcObject) {
                const stream = videoRef.current.srcObject as MediaStream;
                stream.getTracks().forEach(track => track.stop());
            }
        };
    }, []);

    // Camera initialization
    useEffect(() => {
        if (scanMethod === 'camera' && cameraActive) {
            const startCamera = async () => {
                try {
                    const stream = await navigator.mediaDevices.getUserMedia({ 
                        video: { facingMode: 'environment' } 
                    });
                    if (videoRef.current) {
                        videoRef.current.srcObject = stream;
                    }
                } catch (err) {
                    console.error('Camera error:', err);
                    setVerificationResult('Camera access denied. Using manual entry.');
                    setScanMethod('manual');
                    setCameraActive(false);
                }
            };

            startCamera();
        }
    }, [scanMethod, cameraActive]);

    const handleManualInput = (e: React.ChangeEvent<HTMLInputElement>) => {
        setPlateNumber(e.target.value.toUpperCase());
        setVerificationResult(null);
    };

    const handleScan = () => {
        if (!plateNumber.trim()) {
            setVerificationResult('Please enter a valid plate number.');
            return;
        }

        // Simulate API verification
        setTimeout(() => {
            setVerificationResult(`Vehicle ${plateNumber} verified successfully.`);

            // In a real app, you would likely:
            // 1. Send to your backend
            // 2. Add to inspection history
            // 3. Navigate or show next steps
        }, 1000);
    };

    const toggleCamera = () => {
        const newState = !cameraActive;
        setCameraActive(newState);

        // Clear plate when switching camera modes
        if (!newState) {
            setPlateNumber('');
        }
    };

    return (
        <div className="scan-container">
            <h2 className="scan-header">Vehicle Plate Scanner</h2>

            <div className="scan-method-toggle">
                <button
                    className={`method-btn ${scanMethod === 'camera' ? 'active' : ''}`}
                    onClick={() => {
                        setScanMethod('camera');
                        setPlateNumber(''); // Clear when switching modes
                    }}
                >
                    Camera Scan
                </button>
                <button
                    className={`method-btn ${scanMethod === 'manual' ? 'active' : ''}`}
                    onClick={() => {
                        setScanMethod('manual');
                        setCameraActive(false); // Ensure camera is off
                        setPlateNumber(''); // Clear when switching modes
                    }}
                >
                    Manual Entry
                </button>
            </div>

            {scanMethod === 'camera' ? (
                <div className="camera-section">
                    <div className={`camera-feed ${cameraActive ? 'active' : 'inactive'}`}>
                        {cameraActive ? (
                            <>
                                <video 
                                    ref={videoRef} 
                                    autoPlay 
                                    playsInline 
                                    muted
                                    className="video-preview"
                                />
                                <button 
                                    className="capture-btn"
                                    onClick={() => {
                                        // In a real app, this would capture and process the image
                                        alert('Plate capture functionality would go here');
                                    }}
                                >
                                    Capture Plate
                                </button>
                            </>
                        ) : (
                            <div className="camera-placeholder">
                                <span className="camera-icon">ðŸ“·</span>
                                <p>Camera is off</p>
                            </div>
                        )}
                    </div>
                    <button 
                        className={`camera-toggle ${cameraActive ? 'active' : ''}`}
                        onClick={toggleCamera}
                    >
                        {cameraActive ? 'Stop Camera' : 'Start Camera'}
                    </button>
                </div>
            ) : (
                <div className="manual-entry">
                    <input
                        type="text"
                        value={plateNumber}
                        onChange={handleManualInput}
                        placeholder="Enter plate number (e.g. ABC123)"
                        className="plate-input"
                        autoFocus
                    />
                </div>
            )}

            <div className="action-section">
                <button 
                    onClick={handleScan}
                    className="scan-button"
                    disabled={!plateNumber.trim()}
                >
                    {scanMethod === 'camera' ? 'Verify Plate' : 'Submit Plate'}
                </button>
            </div>

            {verificationResult && (
                <div className={`result-message ${verificationResult.includes('success') ? 'success' : 'error'}`}>
                    {verificationResult}
                </div>
            )}

            <canvas ref={canvasRef} style={{ display: 'none' }} />
        </div>
    );
};

export default ScanPlate;
