import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Login button clicked'); // Debugging line
        setError('');
        setIsLoading(true);

        try {
            console.log('Attempting login with:', email, password); // Debugging line

            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1500));

            // DEMO LOGIC - Replace with real authentication
            const isInspector = email.includes('inspector@');
            console.log('isInspector:', isInspector); // Debugging line

            if (isInspector) {
                console.log('Navigating to inspector dashboard'); // Debugging line
                navigate('/inspector/dashboard');
            } else {
                console.log('Navigating to owner dashboard'); // Debugging line
                navigate('/owner/dashboard');
            }

        } catch (err) {
            console.error('Login error:', err); // Debugging line
            setError('Login failed. Please check your credentials.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="login-container">
            <h2>Welcome Back</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleLogin}>
                <div>
                    <label>Email Address</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                        required
                    />
                </div>
                <div>
                    <label>Password</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                        required
                    />
                </div>
                <button 
                    type="submit" 
                    disabled={isLoading}
                    onClick={() => console.log('Button click registered')} // Debugging line
                >
                    {isLoading ? 'Signing In...' : 'Sign In'}
                </button>
            </form>
            <p className="signup-link">
                Don't have an account? <a href="/register">Sign up</a>
            </p>
        </div>
    );
};

export default Login;
