import React, { useState } from 'react';
import VehicleDetails from '../components/Owner/VehicleDetails';
import BookAppointment from '../components/Owner/BookAppointment';
import ViewReports from '../components/Owner/ViewReports';
import './OwnerDashboard.css';

const OwnerDashboard: React.FC = () => {
    // New state for report functionality
    const [report, setReport] = useState({
        type: '',
        description: ''
    });
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleReportSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Report submitted:', report); // Replace with actual API call
        setIsSubmitted(true);
        setTimeout(() => setIsSubmitted(false), 3000);
        setReport({ type: '', description: '' });
    };

    return (
        <div className="owner-dashboard">
            <header className="dashboard-header">
                <h1>Vehicle Owner Dashboard</h1>
                <div className="user-profile">
                    <span className="welcome-message">Welcome back</span>
                    <div className="avatar"></div>
                </div>
            </header>

            <div className="dashboard-grid">
                <section className="dashboard-card vehicle-details-section">
                    <h2>My Vehicle Details</h2>
                    <VehicleDetails />
                </section>

                <section className="dashboard-card appointment-section">
                    <h2>Book Inspection Appointment</h2>
                    <BookAppointment />
                </section>

                <section className="dashboard-card reports-section">
                    <h2>Inspection Reports</h2>
                    <ViewReports />
                </section>

                {/* New Report Section - Added below existing content */}
                <section className="dashboard-card report-section">
                    <h2>Make a Report</h2>
                    {isSubmitted ? (
                        <div className="success-message">Report submitted successfully!</div>
                    ) : (
                        <form onSubmit={handleReportSubmit}>
                            <div className="form-group">
                                <select 
                                    value={report.type}
                                    onChange={(e) => setReport({...report, type: e.target.value})}
                                    required
                                >
                                    <option value="">Select report type</option>
                                    <option value="safety">Safety Concern</option>
                                    <option value="fraud">Suspected Fraud</option>
                                    <option value="service">Service Complaint</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <textarea
                                    value={report.description}
                                    onChange={(e) => setReport({...report, description: e.target.value})}
                                    placeholder="Describe your issue..."
                                    required
                                    rows={4}
                                />
                            </div>
                            <button type="submit" className="submit-button">
                                Submit Report
                            </button>
                        </form>
                    )}
                </section>
            </div>
        </div>
    );
};

export default OwnerDashboard;
