import React, { useEffect, useState } from 'react';
import { getInspectionReports } from '../../api';
import { InspectionReport } from '../../types';

const ViewReports: React.FC = () => {
    const [reports, setReports] = useState<InspectionReport[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchReports = async () => {
            try {
                const fetchedReports = await getInspectionReports();
                console.log('API Response:', fetchedReports); // Keep for debugging
                setReports(fetchedReports);
            } catch (err) {
                setError('Failed to fetch reports');
                console.error('Error fetching reports:', err); // Better error logging
            } finally {
                setLoading(false);
            }
        };

        fetchReports();
    }, []);

    if (loading) return <div className="loading">Loading reports...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="reports-container">
            <h2>Inspection Reports</h2>
            {reports.length === 0 ? (
                <p>No reports available.</p>
            ) : (
                <ul className="reports-list">
                    {reports.map(report => (
                        <li key={report.id} className="report-item">
                            <h3>{report.title || report.reportTitle || 'Untitled Report'}</h3>
                            <p className="report-date">
                                Date: {report.date ? new Date(report.date).toLocaleDateString() : 
                                      report.inspectionDate ? new Date(report.inspectionDate).toLocaleDateString() : 
                                      'Date not specified'}
                            </p>
                            <p className="report-details">
                                {report.details || report.findings || report.comments || 'No details available'}
                            </p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ViewReports;
