import React, { useState } from 'react';
import './Register.css';

const Register: React.FC = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState<'inspector' | 'owner'>('owner');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleRegister = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1500));
            console.log('Registering:', { username, password, role });

            // Replace with actual registration logic:
            // const response = await api.register({ username, password, role });
        } catch (err) {
            setError('Registration failed. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="register-container">
            <h2>Create Your Account</h2>
            {error && <div className="error-message">{error}</div>}
            <form className="register-form" onSubmit={handleRegister}>
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        id="username"
                        type="text"
                        className="form-control"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter your username"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        id="password"
                        type="password"
                        className="form-control"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Create a password"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="role">I am a</label>
                    <select
                        id="role"
                        className="form-control role-select"
                        value={role}
                        onChange={(e) => setRole(e.target.value as 'inspector' | 'owner')}
                    >
                        <option value="owner">Vehicle Owner</option>
                        <option value="inspector">Inspector Officer</option>
                    </select>
                </div>

                <button 
                    type="submit" 
                    className="submit-btn"
                    disabled={isLoading}
                >
                    {isLoading ? (
                        <span className="loading">Creating Account...</span>
                    ) : (
                        'Register Now'
                    )}
                </button>
            </form>

            <p className="login-link">
                Already have an account? <a href="/login">Sign in</a>
            </p>
        </div>
    );
};

export default Register;
