import React, { useState, useRef, useEffect } from 'react';
import './ScanPlate.css';

const ScanPlate: React.FC = () => {
    const [plateNumber, setPlateNumber] = useState('');
    const [verificationResult, setVerificationResult] = useState<string | null>(null);
    const [scanMethod, setScanMethod] = useState<'camera' | 'manual'>('camera');
    const [cameraActive, setCameraActive] = useState(false);
    const [detectedPlate, setDetectedPlate] = useState<string | null>(null);
    const videoRef = useRef<HTMLVideoElement>(null);
    const canvasRef = useRef<HTMLCanvasElement>(null);

    // Mock plate detection (replace with actual OCR/plate recognition)
    const detectPlateFromStream = () => {
        const mockPlates = ['ABC123', 'XYZ789', 'DEF456'];
        const randomPlate = mockPlates[Math.floor(Math.random() * mockPlates.length)];
        setDetectedPlate(randomPlate);
        setPlateNumber(randomPlate);
    };

    // Initialize camera
    useEffect(() => {
        if (scanMethod === 'camera' && cameraActive) {
            const startCamera = async () => {
                try {
                    const stream = await navigator.mediaDevices.getUserMedia({ 
                        video: { facingMode: 'environment' } 
                    });
                    if (videoRef.current) {
                        videoRef.current.srcObject = stream;
                    }

                    // Simulate plate detection every 3 seconds
                    const detectionInterval = setInterval(detectPlateFromStream, 3000);

                    return () => {
                        clearInterval(detectionInterval);
                        stream.getTracks().forEach(track => track.stop());
                    };
                } catch (err) {
                    console.error('Camera error:', err);
                    setVerificationResult('Camera access denied. Please use manual entry.');
                    setScanMethod('manual');
                }
            };

            startCamera();
        }
    }, [scanMethod, cameraActive]);

    const handleScan = () => {
        if (plateNumber) {
            // Simulate API verification
            setTimeout(() => {
                setVerificationResult(`Vehicle ${plateNumber} verified successfully.`);
            }, 1000);
        } else {
            setVerificationResult('Please enter or scan a valid plate number.');
        }
    };

    const toggleCamera = () => {
        setCameraActive(!cameraActive);
        if (!cameraActive) {
            setPlateNumber('');
            setDetectedPlate(null);
        }
    };

    return (
        <div className="scan-container">
            <h2 className="scan-header">Vehicle Plate Scanner</h2>

            <div className="scan-method-toggle">
                <button
                    className={`method-btn ${scanMethod === 'camera' ? 'active' : ''}`}
                    onClick={() => setScanMethod('camera')}
                >
                    Camera Scan
                </button>
                <button
                    className={`method-btn ${scanMethod === 'manual' ? 'active' : ''}`}
                    onClick={() => setScanMethod('manual')}
                >
                    Manual Entry
                </button>
            </div>

            {scanMethod === 'camera' ? (
                <div className="camera-section">
                    <div className={`camera-feed ${cameraActive ? 'active' : 'inactive'}`}>
                        {cameraActive ? (
                            <video 
                                ref={videoRef} 
                                autoPlay 
                                playsInline 
                                muted
                                className="video-preview"
                            />
                        ) : (
                            <div className="camera-placeholder">
                                <span className="camera-icon">ðŸ“·</span>
                                <p>Camera is off</p>
                            </div>
                        )}
                    </div>
                    <button 
                        className={`camera-toggle ${cameraActive ? 'active' : ''}`}
                        onClick={toggleCamera}
                    >
                        {cameraActive ? 'Stop Camera' : 'Start Camera'}
                    </button>

                    {detectedPlate && (
                        <div className="detection-result">
                            <p>Detected Plate: <strong>{detectedPlate}</strong></p>
                            <button 
                                className="confirm-btn"
                                onClick={() => setPlateNumber(detectedPlate)}
                            >
                                Use This Plate
                            </button>
                        </div>
                    )}
                </div>
            ) : (
                <div className="manual-entry">
                    <input
                        type="text"
                        value={plateNumber}
                        onChange={(e) => setPlateNumber(e.target.value)}
                        placeholder="Enter plate number (e.g. ABC123)"
                        className="plate-input"
                    />
                </div>
            )}

            <div className="action-section">
                <button 
                    onClick={handleScan}
                    className="scan-button"
                    disabled={!plateNumber}
                >
                    Verify Plate
                </button>
            </div>

            {verificationResult && (
                <div className={`result-message ${verificationResult.includes('success') ? 'success' : 'error'}`}>
                    {verificationResult}
                </div>
            )}

            <canvas ref={canvasRef} style={{ display: 'none' }} />
        </div>
    );
};

export default ScanPlate;
