import React, { useEffect, useState } from 'react';
import { getInspectionReports } from '../../api';
import { InspectionReport } from '../../types';

const ViewReports: React.FC = () => {
    const [reports, setReports] = useState<InspectionReport[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchReports = async () => {
            try {
                const fetchedReports = await getInspectionReports();
                console.log('Reports:', fetchedReports); // Keep this for debugging
                setReports(fetchedReports);
            } catch (err) {
                setError('Failed to fetch reports');
            } finally {
                setLoading(false);
            }
        };

        fetchReports();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>Your Inspection Reports</h2>
            {reports.length === 0 ? (
                <p>No reports available.</p>
            ) : (
                <ul>
                    {reports.map(report => (
                        <li key={report.id}>
                            <h3>{report.reportTitle || 'Untitled Report'}</h3>
                            <p>Date: {report.inspectionDate ? new Date(report.inspectionDate).toLocaleDateString() : 'Unknown date'}</p>
                            <p>Details: {report.findings || 'No details available'}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ViewReports;
