import React from 'react';
import VehicleDetails from '../components/Owner/VehicleDetails';
import BookAppointment from '../components/Owner/BookAppointment';
import ViewReports from '../components/Owner/ViewReports';
import './OwnerDashboard.css';

interface UserData {
  username: string;
  email: string;
  role: string;
  vehicleDetails?: {
    make: string;
    model: string;
    year: string;
    plateNumber: string;
    color: string;
    vin: string;
  };
}

const OwnerDashboard: React.FC = () => {
  // Get user data from localStorage
  const userData: UserData = JSON.parse(localStorage.getItem('user') || '{}');
  const vehicleDetails = userData.vehicleDetails || {
    make: '',
    model: '',
    year: '',
    plateNumber: '',
    color: '',
    vin: ''
  };

  return (
    <div className="owner-dashboard">
      <header className="dashboard-header">
        <h1>Vehicle Owner Dashboard</h1>
        <div className="user-profile">
          <span className="welcome-message">Welcome back, {userData.username || 'Owner'}</span>
          <div className="avatar">{userData.username?.charAt(0).toUpperCase() || 'O'}</div>
        </div>
      </header>

      <div className="dashboard-grid">
        <section className="dashboard-card vehicle-details-section">
          <h2>My Vehicle Details</h2>
          <VehicleDetails details={vehicleDetails} />
        </section>

        <section className="dashboard-card appointment-section">
          <h2>Book Inspection Appointment</h2>
          <BookAppointment plateNumber={vehicleDetails.plateNumber} />
        </section>

        <section className="dashboard-card reports-section">
          <h2>Inspection Reports</h2>
          <ViewReports vin={vehicleDetails.vin} />
        </section>
      </div>
    </div>
  );
};

export default OwnerDashboard;
