import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

interface VehicleDetails {
    make: string;
    model: string;
    year: string;
    plateNumber: string;
    color: string;
    vin: string;
}

const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState<'inspector' | 'owner'>('owner');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [vehicleDetails, setVehicleDetails] = useState<VehicleDetails>({
        make: '',
        model: '',
        year: '',
        plateNumber: '',
        color: '',
        vin: ''
    });
    const navigate = useNavigate();

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1500));

            // Store vehicle details in localStorage if registering
            if (isRegistering && role === 'owner') {
                localStorage.setItem('vehicleDetails', JSON.stringify(vehicleDetails));
            }

            // Redirect based on selected role
            if (role === 'inspector') {
                navigate('/inspectordashboard', { replace: true });
            } else {
                navigate('/ownerdashboard', { replace: true });
            }

        } catch (err) {
            setError('Login failed. Please check your credentials.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleVehicleDetailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setVehicleDetails(prev => ({
            ...prev,
            [name]: value
        }));
    };

    return (
        <div className="login-container">
            <h2>Vehicle Inspection Portal</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleLogin}>
                <div className="form-group">
                    <label>Email Address</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                        required
                        autoFocus
                    />
                </div>
                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Login As:</label>
                    <div className="radio-group">
                        <label className="radio-option">
                            <input
                                type="radio"
                                name="role"
                                value="owner"
                                checked={role === 'owner'}
                                onChange={() => setRole('owner')}
                            />
                            <span className="radio-label">Vehicle Owner</span>
                        </label>
                        <label className="radio-option">
                            <input
                                type="radio"
                                name="role"
                                value="inspector"
                                checked={role === 'inspector'}
                                onChange={() => setRole('inspector')}
                            />
                            <span className="radio-label">Inspector Officer</span>
                        </label>
                    </div>
                </div>

                {role === 'owner' && (
                    <div className="form-group">
                        <label className="toggle-register">
                            <input
                                type="checkbox"
                                checked={isRegistering}
                                onChange={() => setIsRegistering(!isRegistering)}
                            />
                            <span>Register as new vehicle owner</span>
                        </label>
                    </div>
                )}

                {isRegistering && role === 'owner' && (
                    <div className="vehicle-details-form">
                        <h3>Vehicle Information</h3>
                        <div className="form-group">
                            <label>Make</label>
                            <input
                                type="text"
                                name="make"
                                value={vehicleDetails.make}
                                onChange={handleVehicleDetailChange}
                                placeholder="e.g. Toyota"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Model</label>
                            <input
                                type="text"
                                name="model"
                                value={vehicleDetails.model}
                                onChange={handleVehicleDetailChange}
                                placeholder="e.g. Camry"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Year</label>
                            <input
                                type="text"
                                name="year"
                                value={vehicleDetails.year}
                                onChange={handleVehicleDetailChange}
                                placeholder="e.g. 2020"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Plate Number</label>
                            <input
                                type="text"
                                name="plateNumber"
                                value={vehicleDetails.plateNumber}
                                onChange={handleVehicleDetailChange}
                                placeholder="e.g. ABC123"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Color</label>
                            <input
                                type="text"
                                name="color"
                                value={vehicleDetails.color}
                                onChange={handleVehicleDetailChange}
                                placeholder="e.g. Red"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>VIN</label>
                            <input
                                type="text"
                                name="vin"
                                value={vehicleDetails.vin}
                                onChange={handleVehicleDetailChange}
                                placeholder="Vehicle Identification Number"
                                required
                            />
                        </div>
                    </div>
                )}

                <button 
                    type="submit" 
                    className="login-button"
                    disabled={isLoading || (isRegistering && role === 'owner' && !vehicleDetails.make)}
                >
                    {isLoading ? (
                        <span className="loading">{isRegistering ? 'Registering...' : 'Signing In...'}</span>
                    ) : (
                        isRegistering ? 'Register' : 'Sign In'
                    )}
                </button>
            </form>
        </div>
    );
};

export default Login;
