import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register: React.FC = (): JSX.Element => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [plateNumber, setPlateNumber] = useState('');
    const [role, setRole] = useState<'inspector' | 'owner'>('owner');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleRegister = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            await new Promise(resolve => setTimeout(resolve, 1500));

            const mockResponse = {
                success: true,
                user: {
                    username,
                    role,
                    token: 'mock-token-123'
                }
            };

            if (mockResponse.success) {
                localStorage.setItem('user', JSON.stringify(mockResponse.user));
                navigate(role === 'inspector' ? '/inspector-dashboard' : '/owner-dashboard');
            }
        } catch (err) {
            setError('Registration failed. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="register-container">
            <h2>Create Your Account</h2>
            {error && <div className="error-message">{error}</div>}
            <form className="register-form" onSubmit={handleRegister}>
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        id="username"
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter your username"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        id="password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Create a password"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="role">I am a</label>
                    <select
                        id="role"
                        value={role}
                        onChange={(e) => setRole(e.target.value as 'inspector' | 'owner')}
                    >
                        <option value="owner">Vehicle Owner</option>
                        <option value="inspector">Inspector Officer</option>
                    </select>
                </div>

                {role === 'owner' && (
                    <div className="form-group">
                        <label htmlFor="plateNumber">License Plate Number</label>
                        <input
                            id="plateNumber"
                            type="text"
                            value={plateNumber}
                            onChange={(e) => setPlateNumber(e.target.value)}
                            placeholder="Enter your vehicle plate number"
                            required={role === 'owner'}
                        />
                    </div>
                )}

                <button 
                    type="submit" 
                    disabled={isLoading || (role === 'owner' && !plateNumber)}
                >
                    {isLoading ? 'Creating Account...' : 'Register Now'}
                </button>
            </form>

            <p className="login-link">
                Already have an account? <a href="/login">Sign in</a>
            </p>
        </div>
    );
};

export default Register;
