import React, { useEffect, useState } from 'react';
import { getReports } from '../../api'; // Changed from getInspectionReports
import { InspectionReport } from '../../types';

const ViewReports: React.FC = () => {
    const [reports, setReports] = useState<InspectionReport[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchReports = async () => {
            try {
                const fetchedReports = await getReports();
                console.log('Fetched reports:', fetchedReports); // Debugging
                setReports(fetchedReports);
            } catch (err) {
                setError('Failed to fetch reports');
                console.error('Error fetching reports:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchReports();
    }, []);

    if (loading) {
        return <div>Loading reports...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="reports-container">
            <h2>Your Inspection Reports</h2>
            {reports.length === 0 ? (
                <p>No reports available.</p>
            ) : (
                <ul className="reports-list">
                    {reports.map(report => (
                        <li key={report.id} className="report-item">
                            {/* Safe property access with fallbacks */}
                            <h3>{report.title || report.reportTitle || 'Untitled Report'}</h3>
                            <p className="report-date">
                                {report.date ? new Date(report.date).toLocaleDateString() : 
                                 report.inspectionDate ? new Date(report.inspectionDate).toLocaleDateString() : 
                                 'Date not available'}
                            </p>
                            <p className="report-details">
                                {report.details || report.findings || 'No details provided'}
                            </p>
                            {report.status && (
                                <p className={`report-status ${report.status}`}>
                                    Status: {report.status}
                                </p>
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ViewReports;
