import React, { useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import Sidebar from '../components/Shared/Sidebar';
import Header from '../components/Shared/Header';
import './Dashboard.css'; // Shared dashboard styles

const InspectorDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState('scan');
  const navigate = useNavigate();

  const handleTabChange = (tab: string) => {
    setActiveTab(tab);
    navigate(`/inspector/${tab}`);
  };

  return (
    <div className="dashboard-container">
      <Sidebar 
        userType="inspector" 
        activeTab={activeTab}
        onTabChange={handleTabChange}
      />

      <div className="main-content">
        <Header title="Inspector Dashboard" />

        <div className="dashboard-content">
          {/* Tab-based content rendering */}
          {activeTab === 'scan' && <ScanPlate />}
          {activeTab === 'history' && <InspectionHistory />}
          {activeTab === 'upload' && <UploadReport />}

          {/* OR use React Router's Outlet if you prefer route-based rendering */}
          {/* <Outlet /> */}
        </div>
      </div>
    </div>
  );
};

export default InspectorDashboard;
