import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BookAppointment from '../components/Owner/BookAppointment';
import ViewReports from '../components/Owner/ViewReports';
import './OwnerDashboard.css';

const OwnerDashboard: React.FC = () => {
    const navigate = useNavigate();
    const [report, setReport] = useState({
        type: '',
        description: ''
    });
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [userData, setUserData] = useState<any>(null);
    const [vehicleData, setVehicleData] = useState<any>(null);

    // Check authentication and load data on component mount
    useEffect(() => {
        const checkAuth = async () => {
            const token = localStorage.getItem('authToken');
            const userRole = localStorage.getItem('userRole');

            if (!token || userRole !== 'owner') {
                navigate('/login', { replace: true });
                return;
            }

            try {
                // Simulate API calls to fetch user and vehicle data
                await new Promise(resolve => setTimeout(resolve, 1000));

                // Mock data - replace with actual API calls
                setUserData({
                    name: "John Doe",
                    email: "john@example.com",
                    phone: "+1234567890"
                });

                setVehicleData({
                    make: "Toyota",
                    model: "Camry",
                    year: "2020",
                    licensePlate: "ABC1234",
                    lastInspection: "2023-05-15"
                });

            } catch (error) {
                console.error("Failed to load data:", error);
            } finally {
                setIsLoading(false);
            }
        };

        checkAuth();
    }, [navigate]);

    const handleReportSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            // Simulate API call to submit report
            await new Promise(resolve => setTimeout(resolve, 800));
            console.log('Report submitted:', report);
            setIsSubmitted(true);
            setTimeout(() => setIsSubmitted(false), 3000);
            setReport({ type: '', description: '' });
        } catch (error) {
            console.error("Failed to submit report:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleLogout = () => {
        // Clear all user-related data
        localStorage.removeItem('authToken');
        localStorage.removeItem('userRole');
        localStorage.removeItem('userData');

        // Redirect to login page
        navigate('/login');
    };

    if (isLoading) {
        return (
            <div className="loading-overlay">
                <div className="loading-spinner"></div>
                <p>Loading dashboard...</p>
            </div>
        );
    }

    return (
        <div className="owner-dashboard">
            <header className="dashboard-header">
                <div className="user-info">
                    <h1>Vehicle Owner Dashboard</h1>
                    {userData && (
                        <div className="user-profile">
                            <span className="user-name">{userData.name}</span>
                            <span className="user-email">{userData.email}</span>
                        </div>
                    )}
                </div>
                <button 
                    onClick={handleLogout} 
                    className="logout-button"
                    disabled={isLoading}
                >
                    {isLoading ? 'Logging out...' : 'Logout'}
                </button>
            </header>

            <div className="dashboard-grid">
                <section className="dashboard-card vehicle-details-section">
                    <h2>My Vehicle Details</h2>
                    {vehicleData ? (
                        <div className="vehicle-details">
                            <div className="detail-row">
                                <span className="detail-label">Make:</span>
                                <span className="detail-value">{vehicleData.make}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label">Model:</span>
                                <span className="detail-value">{vehicleData.model}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label">Year:</span>
                                <span className="detail-value">{vehicleData.year}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label">License Plate:</span>
                                <span className="detail-value">{vehicleData.licensePlate}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label">Last Inspection:</span>
                                <span className="detail-value">{vehicleData.lastInspection}</span>
                            </div>
                        </div>
                    ) : (
                        <div className="empty-section">
                            No vehicle information available
                        </div>
                    )}
                </section>

                <section className="dashboard-card appointment-section">
                    <h2>Book Inspection Appointment</h2>
                    <BookAppointment />
                </section>

                <section className="dashboard-card reports-section">
                    <h2>Inspection Reports</h2>
                    <ViewReports />
                </section>

                <section className="dashboard-card report-section">
                    <h2>Make a Report</h2>
                    {isSubmitted ? (
                        <div className="success-message">
                            <span>âœ“</span> Report submitted successfully!
                        </div>
                    ) : (
                        <form onSubmit={handleReportSubmit}>
                            <div className="form-group">
                                <label>Report Type</label>
                                <select
                                    value={report.type}
                                    onChange={(e) => setReport({...report, type: e.target.value})}
                                    required
                                >
                                    <option value="">Select type</option>
                                    <option value="complaint">Complaint</option>
                                    <option value="suggestion">Suggestion</option>
                                    <option value="technical">Technical Issue</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Description</label>
                                <textarea
                                    value={report.description}
                                    onChange={(e) => setReport({...report, description: e.target.value})}
                                    placeholder="Describe your issue..."
                                    required
                                />
                            </div>
                            <button 
                                type="submit" 
                                className="submit-button"
                                disabled={isLoading}
                            >
                                {isLoading ? 'Submitting...' : 'Submit Report'}
                            </button>
                        </form>
                    )}
                </section>
            </div>
        </div>
    );
};

export default OwnerDashboard;
