import React from 'react';
import VehicleDetails from '../components/Owner/VehicleDetails';
import BookAppointment from '../components/Owner/BookAppointment';
import './OwnerDashboard.css';

interface VehicleDetailsType {
  make: string;
  model: string;
  year: string;
  plateNumber: string;
  color: string;
  vin: string;
}

interface UserData {
  username: string;
  email: string;
  role: string;
  vehicleDetails?: VehicleDetailsType;
}

const defaultVehicleDetails: VehicleDetailsType = {
  make: 'Not specified',
  model: 'Not specified',
  year: 'Not specified',
  plateNumber: 'Not specified',
  color: 'Not specified',
  vin: 'Not specified'
};

const OwnerDashboard: React.FC = () => {
  const getUserData = (): UserData => {
    try {
      const data = localStorage.getItem('user');
      return data ? JSON.parse(data) : { vehicleDetails: defaultVehicleDetails };
    } catch (error) {
      console.error('Error parsing user data:', error);
      return { vehicleDetails: defaultVehicleDetails };
    }
  };

  const userData = getUserData();
  const vehicleDetails = userData.vehicleDetails || defaultVehicleDetails;

  return (
    <div className="owner-dashboard">
      <header className="dashboard-header">
        <h1>Vehicle Owner Dashboard</h1>
        <div className="user-profile">
          <span className="welcome-message">
            Welcome back, {userData.username || 'Owner'}
          </span>
          <div className="avatar">
            {userData.username?.charAt(0).toUpperCase() || 'O'}
          </div>
        </div>
      </header>

      <div className="dashboard-grid">
        <section className="dashboard-card">
          <h2>My Vehicle Details</h2>
          <VehicleDetails 
            make={vehicleDetails.make}
            model={vehicleDetails.model}
            year={vehicleDetails.year}
            plateNumber={vehicleDetails.plateNumber}
            color={vehicleDetails.color}
            vin={vehicleDetails.vin}
          />
        </section>

        <section className="dashboard-card">
          <h2>Book Inspection</h2>
          <BookAppointment plateNumber={vehicleDetails.plateNumber} />
        </section>
      </div>
    </div>
  );
};

export default OwnerDashboard;
