import React, { useState, useRef, useEffect } from 'react';
import './ScanPlate.css';

// Add this interface for scan history items
interface ScanHistoryItem {
  id: string;
  plateNumber: string;
  timestamp: Date;
  status: 'verified' | 'rejected';
  message: string;
}

const ScanPlate: React.FC = () => {
    const [plateNumber, setPlateNumber] = useState('');
    const [verificationResult, setVerificationResult] = useState<string | null>(null);
    const [scanMethod, setScanMethod] = useState<'camera' | 'manual'>('manual');
    const [cameraActive, setCameraActive] = useState(false);
    const [isValidPlate, setIsValidPlate] = useState<boolean | null>(null);
    const [capturedImage, setCapturedImage] = useState<string | null>(null);
    const [userEnteredPlate, setUserEnteredPlate] = useState('');
    const [scanHistory, setScanHistory] = useState<ScanHistoryItem[]>([]); // Added history state
    const videoRef = useRef<HTMLVideoElement>(null);
    const canvasRef = useRef<HTMLCanvasElement>(null);

    // Enhanced plate validation
    const PLATE_REGEX = /^[A-Z]{1,3}[ -]?\d{1,4}[A-Z]?$/i;

    const validatePlateNumber = (plate: string): boolean => {
        if (!plate) return false;
        const cleanPlate = plate.replace(/[ -]/g, '');
        const patternValid = PLATE_REGEX.test(plate);
        const hasLetters = /[A-Z]/i.test(cleanPlate);
        const hasNumbers = /\d/.test(cleanPlate);
        const minLength = cleanPlate.length >= 4;
        return patternValid && hasLetters && hasNumbers && minLength;
    };

    // Add to history function
    const addToHistory = (plate: string, status: 'verified' | 'rejected', message: string) => {
        const newItem: ScanHistoryItem = {
            id: Date.now().toString(),
            plateNumber: plate,
            timestamp: new Date(),
            status,
            message
        };
        setScanHistory(prev => [newItem, ...prev]);
        // Optional: Save to localStorage
        localStorage.setItem('scanHistory', JSON.stringify([newItem, ...scanHistory]));
    };

    // Load history from localStorage on mount
    useEffect(() => {
        const savedHistory = localStorage.getItem('scanHistory');
        if (savedHistory) {
            setScanHistory(JSON.parse(savedHistory));
        }
    }, []);

    // Clean up camera
    useEffect(() => {
        return () => {
            if (videoRef.current?.srcObject) {
                const stream = videoRef.current.srcObject as MediaStream;
                stream.getTracks().forEach(track => track.stop());
            }
        };
    }, []);

    // Camera initialization
    useEffect(() => {
        if (scanMethod === 'camera' && cameraActive) {
            const startCamera = async () => {
                try {
                    const stream = await navigator.mediaDevices.getUserMedia({ 
                        video: { facingMode: 'environment' } 
                    });
                    if (videoRef.current) {
                        videoRef.current.srcObject = stream;
                    }
                } catch (err) {
                    console.error('Camera error:', err);
                    setVerificationResult('Camera access denied. Using manual entry.');
                    setScanMethod('manual');
                    setCameraActive(false);
                }
            };
            startCamera();
        }
    }, [scanMethod, cameraActive]);

    const captureImage = () => {
        if (videoRef.current && canvasRef.current) {
            const context = canvasRef.current.getContext('2d');
            if (context) {
                canvasRef.current.width = videoRef.current.videoWidth;
                canvasRef.current.height = videoRef.current.videoHeight;
                context.drawImage(videoRef.current, 0, 0);
                const imageUrl = canvasRef.current.toDataURL('image/jpeg');
                setCapturedImage(imageUrl);
                setVerificationResult("Processing image...");
                setTimeout(() => {
                    setVerificationResult("Please confirm the plate number");
                    setUserEnteredPlate('');
                }, 1500);
            }
        }
    };

    const handleManualInput = (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value.toUpperCase();
        setPlateNumber(value);
        setVerificationResult(null);
        setIsValidPlate(validatePlateNumber(value));
    };

    const handleManualCorrection = (e: React.ChangeEvent<HTMLInputElement>) => {
        setUserEnteredPlate(e.target.value.toUpperCase());
    };

    const confirmPlateNumber = () => {
        if (userEnteredPlate) {
            setPlateNumber(userEnteredPlate);
            setIsValidPlate(validatePlateNumber(userEnteredPlate));
            setVerificationResult(null);
        }
    };

    const handleScan = () => {
        if (!plateNumber.trim()) {
            const errorMsg = 'Please enter a plate number';
            setVerificationResult(errorMsg);
            setIsValidPlate(false);
            addToHistory('', 'rejected', errorMsg);
            return;
        }

        const isValid = validatePlateNumber(plateNumber);
        setIsValidPlate(isValid);

        if (!isValid) {
            const errorMsg = 'Invalid plate number format';
            setVerificationResult(errorMsg);
            addToHistory(plateNumber, 'rejected', errorMsg);
            return;
        }

        setVerificationResult("Verifying plate...");
        setTimeout(() => {
            const successMsg = `Vehicle ${plateNumber} verified successfully`;
            setVerificationResult(successMsg);
            addToHistory(plateNumber, 'verified', successMsg);
        }, 1000);
    };

    const toggleCamera = () => {
        setCameraActive(!cameraActive);
        if (!cameraActive) {
            setPlateNumber('');
            setCapturedImage(null);
            setIsValidPlate(null);
            setVerificationResult(null);
        }
    };

    return (
        <div className="scan-container">
            <h2>Vehicle Plate Scanner</h2>

            <div className="scan-method-toggle">
                <button
                    className={`method-btn ${scanMethod === 'camera' ? 'active' : ''}`}
                    onClick={() => setScanMethod('camera')}
                >
                    Camera Scan
                </button>
                <button
                    className={`method-btn ${scanMethod === 'manual' ? 'active' : ''}`}
                    onClick={() => setScanMethod('manual')}
                >
                    Manual Entry
                </button>
            </div>

            {/* ... rest of your existing JSX ... */}

            {/* Add this section to show recent scans */}
            <div className="recent-scans">
                <h3>Recent Scans</h3>
                <ul>
                    {scanHistory.slice(0, 5).map(scan => (
                        <li key={scan.id} className={`scan-item ${scan.status}`}>
                            <span className="scan-time">
                                {scan.timestamp.toLocaleTimeString()}
                            </span>
                            <span className="scan-plate">
                                {scan.plateNumber || 'N/A'}
                            </span>
                            <span className="scan-status">
                                {scan.status.toUpperCase()}
                            </span>
                        </li>
                    ))}
                </ul>
            </div>

            <canvas ref={canvasRef} style={{ display: 'none' }} />
        </div>
    );
};

export default ScanPlate;
